import React, { useEffect, useState } from 'react';

interface Code {
  title: string;
  link: string;
}

const Header: React.FC = () => {
  const [codes, setCodes] = useState<Code[]>([]);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetch('/api/trending-codes')
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => setCodes(data))
      .catch((error) => {
        console.error('Error fetching trending codes:', error);
        setError('Unable to fetch trending codes. Please try again later.');
      });
  }, []);

  return (
    <header>
      <div>
        <h1>Trending Codes on Kaggle</h1>
        {error ? (
          <p>{error}</p>
        ) : (
          <ul>
            {codes.map((code, index) => (
              <li key={index}>
                <a href={code.link} target="_blank" rel="noopener noreferrer">
                  {code.title}
                </a>
              </li>
            ))}
          </ul>
        )}
      </div>
    </header>
  );
};

export default Header;
