import React, { useEffect, useState } from 'react';
import '../styles/Header.css';
import swordIcon from '../assets/images/sword.png';

interface Competition {
  title: string;
  url: string;
  deadline: string;
}

const Header: React.FC = () => {
  const [competitions, setCompetitions] = useState<Competition[]>([]);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetch('/api/competitions')
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.text(); // Yanıtı düz metin olarak al
      })
      .then((text) => {
        try {
          const data = JSON.parse(text); // JSON formatında ayrıştır
          setCompetitions(data);
        } catch (parseError) {
          console.error('Error parsing JSON:', parseError);
          setError('Failed to parse JSON response.');
        }
      })
      .catch((fetchError) => {
        console.error('Error fetching competitions:', fetchError);
        setError('Failed to fetch competitions. Please try again later.');
      });
  }, []);

  return (
    <>
      <header className="header">
        <div className="container">
          <div className="logo">
            burakkanber
            <img src={swordIcon} alt="Sword Icon" />
          </div>
          <nav className="nav">
            <ul>
              <li><a href="#news">news</a></li>
              <li><a href="#buymecoffee">buy me coffee</a></li>
              <li><a href="#docs">idk</a></li>
              <li><a href="#contact">contact</a></li>
            </ul>
          </nav>
        </div>
      </header>
      <div className="scrolling-header">
        {error ? (
          <p className="error-message">{error}</p>
        ) : (
          <ul>
            {competitions.map((competition, index) => (
              <li key={index}>
                <a href={competition.url} target="_blank" rel="noopener noreferrer">
                  {competition.title} - {new Date(competition.deadline).toLocaleDateString()}
                </a>
              </li>
            ))}
          </ul>
        )}
      </div>
    </>
  );
};

export default Header;
